mapper.select:
    	select count(usertel) cnt from member

			where 			${searchKey}
			like 			'%${searchWord}%'

    	select count(contentId) cnt from campinglist

			where 			${searchKey}
			like 			'%${searchWord}%'

    	select count(carno) cnt from car

			where 			${searchKey}
			like 			'%${searchWord}%'

    	select count(gatherno) cnt from gather

			where 			${searchKey}
			like 			'%${searchWord}%'

    	select count(replyno) cnt from gatherreply

			where 			${searchKey}
			like 			'%${searchWord}%'

    	select count(replyno) cnt from carreply

			where 			${searchKey}
			like 			'%${searchWord}%'

        SELECT userid, username, nickname, email, case isadmin when 0 then '사용자' else '관리자'end AS isadmin, userscore, usertel, joindate from member

		where 				${searchKey}
		like 				'%${searchWord}%'

        order by joindate desc
        limit		 		${onePageRecord}
		offset				${offsetIndex}

    	select contentId, facltNm, addr1, addr2, tel, homepage, resveCl, resveUrl from campinglist

		where 				${searchKey}
		like 				'%${searchWord}%'

    	order by contentId desc
    	limit		 		${onePageRecord}
		offset				${offsetIndex}

    	select gatherno, title, place, nickname, startdate, enddate, createdate from gather

		where 				${searchKey}
		like 				'%${searchWord}%'

    	order by gatherno desc
    	limit		 		${onePageRecord}
		offset				${offsetIndex}

    	select carno, title, content, writedate, nickname, region, locx, locy from car

		where 				${searchKey}
		like 				'%${searchWord}%'

    	order by carno desc
    	limit		 		${onePageRecord}
		offset				${offsetIndex}

    	select replyno, content, writedate, nickname, gatherno from gatherreply

		where 				${searchKey}
		like 				'%${searchWord}%'

    	order by replyno desc
    	limit		 		${onePageRecord}
		offset				${offsetIndex}

    	select replyno, content, writedate, nickname, carno from carreply

		where 				${searchKey}
		like 				'%${searchWord}%'

    	order by replyno desc
    	limit		 		${onePageRecord}
		offset				${offsetIndex}

        SELECT userid, username, nickname, email, case isadmin when 0 then '관리자' else '사용자'end AS isadmin, userscore, usertel, joindate from member

		where 				${searchKey}
		like 				'%${searchWord}%'

        order by joindate desc
        limit		 		${onePageRecord}
		offset				${offsetIndex}
