mapper.select:
    	select contentId, facltNm, addr1, addr2, tel, homepage, resveCl, resveUrl from campinglist

		where 				${searchKey}
		like 				'%${searchWord}%'

    	order by contentId desc
    	limit		 		${onePageRecord}
		offset				${offsetIndex}

    	select count(contentId) cnt from campinglist

			where 			${searchKey}
			like 			'%${searchWord}%'
