mapper.select:
SELECT gnewno FROM gather 
GROUP BY gatherno 
HAVING gatherno=#{param1}

SELECT gatherno, title, nickname, place, views, date_format(createdate, '%m-%d %H:%i') createdate FROM gather
WHERE ${searchKey} LIKE '%${searchWord}%'
ORDER BY gatherno DESC LIMIT ${onePageRecord} OFFSET ${offsetIndex}

SELECT gmemberno, nickname, gatherno FROM gathermember
WHERE ${searchKey} LIKE '%${searchWord}%'
ORDER BY gatherno DESC LIMIT ${onePageRecord} OFFSET ${offsetIndex}
